import './App.css';
import 'font-awesome/css/font-awesome.css'
import {useState, View} from 'react';
import AdminLogin from './layout/admin/Admin';
import Site from './layout/site/Site';
import Login from './pages/admin/login/Login'
import { BrowserRouter, Navigate, Route, Routes } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

function App() {

  const user = useSelector((state) => state.auth.user)
  const dispatch = useDispatch();

  return (
    <BrowserRouter>
      <Routes>
        <Route path='/' element={<Site></Site>}></Route>
        <Route path='/admin/login' element={<AdminLogin></AdminLogin>}></Route>
        <Route path='/signup'></Route>
        <Route path='/admin' element={user.userType == 1 ? <Admin></Admin> : <Navigate to='login'></Navigate>} ></Route>
      </Routes>
    </BrowserRouter>
  )

}

export default App;
