import 'bootstrap/dist/css/bootstrap.min.css'
import { useState } from 'react';
import adminLogin from '../../../api/auth/adminAuth';

const AdminLogin = () => {

    const  [email, setEmail] = useState("");
    const  [password, setPassword] = useState("");

    const handleLogin = () => {
        adminLogin(email, password).then(res => {
            if(res.status == 200)
                alert("Giriş Başarılı!");
            else if(res.status == 500)
                alert("Geçersiz Kullanıcı Adı veya Şifre!");
        });
    }

    const handleEmail = (event) => {
        setEmail(event.target.value);
    }

    const handlePassword = (event) => {
        setPassword(event.target.value);
    }

    return(
    <div className="container d-flex jusify-content-center">
        <div className='vertical-center horizontal-center'>
            <div className='card'>
                <div className='card-header text-center'>
                    Giriş Yap
                </div>
                <div className='card-body'>
                    <div className='input-group form-group'>
                        <div class="input-group-prepend">
                            <span className="input-group-text"><i className="fa fa-user" aria-hidden="true"></i></span>
                        </div>
                        <input id='username' placeholder='E-Mail' type='text' onChange={handleEmail}></input>
                    </div>

                    <div className='input-group form-group'>
                        <div className="input-group-prepend">
                            <span className="input-group-text"><i className="fa fa-xl fa-lock"></i></span>
                        </div>
                        <input id='password' placeholder='Şifre' type='password' onChange={handlePassword}></input>
                    </div>

                    <div className='form-group text-center'>
                        <button className='btn btn-primary' onClick={handleLogin} id='submit' type='button'>Giriş Yap</button>
                    </div>
                </div>
            </div>
        </div>
    </div>   
    );
}

export default AdminLogin;
