import { createSlice } from "@reduxjs/toolkit";

const authSlice = createSlice({
	name: 'auth',
	initialState: {
		isLoggedIn: false,
		user: null,
	},
	reducers: {
		login: (state, action) => {
			state.user = action.payload;
		},
		logout: (state, action) => {
			state.user = null;
			state.isLoggedIn = true;
	    	}
	}
});

export const {login, logout} = authSlice.actions;

export default authSlice.reducer;
