import './App.css';
import {useState, View} from 'react';
import Admin from './layout/admin/Admin';
import Site from './layout/site/Site';
import Login from './pages/login/Login'
import { BrowserRouter, Navigate, Route, Routes } from 'react-router-dom';

function App() {

  const [user, setUser] = useState();

  const isAdmin = () => {
    return user && user.userType == 1
  }

  return (
    <BrowserRouter>
      <Routes>
        <Route path='/' element={<Site></Site>}></Route>
        <Route path='/login' element={Login}></Route>
        <Route path='/signup'></Route>
        <Route path='/admin' element={isAdmin() ? <Admin></Admin> : <Navigate to='/login'></Navigate>} ></Route>
      </Routes>
    </BrowserRouter>
  )

}

export default App;
