import './App.css';
import {useState, View} from 'react';
import Admin from './layout/admin/Admin';
import Site from './layout/site/Site';
import Login from './pages/login/Login'

function App() {

  const [user, setUser] = useState();

  if(user){
    if(user.userType == 1){
      return <Admin></Admin>
    } 
    else{
      return <Site></Site>
    }     
  }
  else {
    return (
    <div className='App'><Login></Login></div>)
  }
}

export default App;
